		emit(doc.username, doc.token);
	}
}"},"getChat":{"map":"function (doc) {
	if (doc.type == "message") {
		if (doc.ts) {
			emit([doc.user, doc.ts], {user: doc.user, dir: doc.dir, message: doc.message, ts: doc.ts});
		} else {
			emit([doc.user, doc.num], {user: doc.user, dir: doc.dir, message: doc.message, num: doc.num});
		}
	}
}"},"typeMessage":{"map":"function (doc) {
